mapper.select:
select * from tb_tag where 1=1

    and userId=#{userId}


    and tagName LIKE CONCAT(CONCAT('%', #{keyword}), '%')

order by sortType,firstIndex ASC
limit #{index},#{size}

select COUNT(*) from tb_tag where 1=1

    and userId=#{userId}


    and tagName LIKE CONCAT(CONCAT('%', #{keyword}), '%')

select * from tb_tag where userId=#{userId} and id in (
select tagId from tb_tag_source where userId = #{userId} and sourceId = #{sourceId} and sourceType=#{sourceType}
)
