mapper.select:
select DISTINCT * from tb_tag_source where 1=1

    and userId=#{userId}


    and sourceType = #{sourceType}


    and tagId = #{tagId}


    and sourceTitle LIKE CONCAT(CONCAT('%', #{keyword}), '%');

order by createAt DESC
limit #{index},#{size}

select DISTINCT COUNT(*) from tb_tag_source where 1=1

    and userId=#{userId}


    and sourceType = #{sourceType}


    and tagId = #{tagId}


    and sourceTitle LIKE CONCAT(CONCAT('%', #{keyword}), '%');
