mapper.delete:
DELETE FROM tb_tag_source where userId=#{userId} AND tagId = #{tagId}

    and sourceType = #{sourceType}


    and sourceId = #{sourceId}

mapper.select:
SELECT s.* from tb_tag t,tb_tag_source s  where t.id=s.tagId

    and s.userId=#{userId}


    and s.sourceType = #{sourceType}


    and s.tagId = #{tagId}


    and （s.sourceTitle LIKE CONCAT(CONCAT('%', #{keyword}), '%') OR t.tagName LIKE CONCAT(CONCAT('%', #{keyword}), '%') )

GROUP BY s.sourceId,s.sourceType
order by s.createAt DESC
limit #{index},#{size}

SELECT COUNT(*) from tb_tag_source WHERE userId=#{userId}
AND id IN (
    SELECT s.id from tb_tag t,tb_tag_source s  where t.id=s.tagId

        and s.userId=#{userId}


        and s.sourceType = #{sourceType}


        and s.tagId = #{tagId}


        and （s.sourceTitle LIKE CONCAT(CONCAT('%', #{keyword}), '%') OR t.tagName LIKE CONCAT(CONCAT('%', #{keyword}), '%') )

    GROUP BY s.sourceId,s.sourceType
)

select * from tb_tag_source where 1=1

    and userId=#{userId}


    and sourceType = #{sourceType}


    and sourceTitle LIKE CONCAT(CONCAT('%', #{keyword}), '%')

and tagId not IN (
     SELECT id FROM tb_tag where userId=#{userId} and tagName LIKE CONCAT(CONCAT('%', #{keyword}), '%')
)
GROUP BY sourceId,sourceType
order by createAt DESC
